    private void btndivisorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btndivisorActionPerformed
        float n = Float.valueOf(txtnumerador.getText());
        float d = Float.valueOf(txtdenominador.getText());
        float divisao = n/d ;
        float resto =(float) Math.sqrt(n);
        lbldivisao.setText(Float.toString(divisao));
        lblres.setText(Float.toString(resto));
    }//GEN-LAST:event_btndivisorActionPerformed

